.article-contents {
    font-family: "Georgia", serif;
    font-size: 1.1em;
    line-height: 1.55em;
    color: black;
}
 
.article-contents blockquote {
    background: white;
    font-style: normal;
    margin: 1em;
}
 
.articles li {
    padding: 0.25em;
}
 
.current-article .feed-title {
    margin-left: 0;
}
 
.current-article .article-title {
    margin-left: -27px;
}
 
.article-contents hr {
    margin: 1em 0;
}
 
.article-list-header {
    padding-top: 30px;
}
 
.content {
    padding-top: 30px;
}
 
.articles-list-container {
    top: 66px;
}
 
.content.padded-bottom-3 {
    padding-bottom: 0;
}
