css:
/* name: GENO web liquid style 2013/06 */
@-moz-document domain('geno-web.jp') {

body {background: none !important;}
#M_wrapper, #M_header {width: auto !important;}
table[height*="0"], td[height*="0"] {height: auto !important;}
#M_header table, #M_header td, table#makebanner {width: 100% !important;}
#M_leftContents, #M_leftContents div {width: auto !important;}
td[width="640"] {width: auto !important;}
table[width="810"], table[width="800"] {width: 100% !important;}
td.woong, td.lims {width: auto !important;}
img[width="750"] {width: 100% !important;}

.M_mainContents {
 float: none !important;
 width: auto !important; padding: 0 !important;
 }
.subcat_link {width: auto !important; margin: 0 !important; padding: 0 !important;}
#M_categoryList table,
#M_categoryList table tr td,
#M_categoryList table tr td table
 {width: auto !important;}
}
