@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(m.sweclockers.com){

.wrapinner{background:#202020 !important;}
#header{background:#404040 !important;}
.mainmenu{
  background:-webkit-gradient(linear,left top,left bottom,from(#404040),color-stop(45%,#404040),color-stop(100%,#252525),to(#303030)) !important;
  background:-moz-linear-gradient(top,#404040,#404040 45%,#252525 100%,#303030) !important;
}
.headMain{
  background:-webkit-gradient(linear,left top,left bottom,from(#606060),color-stop(98%,#404040),to(#353535)) !important;
  background:-moz-linear-gradient(top,#606060,#404040 98%,#353535) !important;
}
.itemList{background:#505050 !important; border: 1px solid #808080 !important;}
.itemList>li{border-top: 1px solid #808080 !important;}
.itemList li:first-child{border:0 !important;}
.title>h1{color:#bbbbbb !important;}
.preamble>.bbcode>p{color:#bbbbbb !important;}
.contentBox{background:#aaaaaa !important;}
.bbcode>p>a{color:#1c2f80 !important; text-decoration:underline;}
.logo>span{background-image: url(http://alphaapplication.com/images/swec-logo.png) !important;}
.row>a{color:#ddd !important;}
/*.articleStatus{background-image: url('http://alphaapplication.com/images/swec-cat.png') !important;}*/
.inner>h2{color:#eee !important;}
ul.itemList .cell.status>span{border-top:2px solid #606060 !important;}
.profileAuth{color:#ddd !important;}
.profileAuth>h6{color:#ddd !important;}
.descr{color:#ddd !important;}
.galleryList>ul>li>a{color:#aaaaaa !important;}
.nodeInner{background:#505050 !important; border:1px solid #808080 !important;}
.forumPost>.body{background: #101010 !important;color:#8a8a8a !important}
}
