css:
@-moz-document url-prefix("http://ptv.vic.gov.au/journey") {
#MapMenu {
  max-width: 1162px;
  margin: 0 auto;
  position: relative;
}



@media (max-width: 900px) {
#MapMenu {
  width:337px;
}
}


@media (min-width: 900px) and (max-width: 1180px) {
#journeyPlannerPanel {
  float: left !important;
}
#MapMenu {
  max-width: 850px;
}
}

}
