css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("reddit.com") {
    div.midcol,span.domain{display:none!important;}
    span.rank{padding-right:2%!important;}
    div.thing{border-bottom:1px solid #aaaaaa!important;}
    div.infobar.welcome{display:none!important;}
    a.comments{font-size:1.3em!important;}
    p.tagline{display:none!important;}
    ul.buttons a:visited{color:#8800aa!important;}
    a.title:visited{color:#8800aa!important;}
    
    /* stupid r/league sidebar */
    div.md blockquote:last-of-type{display:none!important;}
    div.md h1:last-of-type{display:none!important;}
    div.md p:nth-child(4){display:none!important;}
    div.md h1:first-of-type{display:none!important;}
    div.md h6:first-of-type{display:none!important;}
    div.md h5:first-of-type{display:none!important;}
    div.md h4:first-of-type{display:none!important;}
    div.md blockquote:first-of-type{display:none!important;}
    div.md blockquote:nth-of-type(2){display:none!important;}
    div.md blockquote:nth-of-type(3){display:none!important;}
    #ad_main{display:none!important;}
}
