css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain('slovnik.seznam.cz') {

/* podklad */
div.move, body, div#page {
background: #073642 !important;}

/* vstupní formulář */
input#q {
background: #073642 !important;
border-radius: 6px !important;
border-color: #586e75 !important;
box-shadow: 0 0 10px #657b83 !important;}

/* slovo, základní fráze/pokročilá gramatika */
h3, h3.recommendation {
color: #93a1a1 !important;
background: #073642 !important;}

/* prostý text */
.text {
color: #93a1a1 !important}

/* odrážky, český text vět v základních frázích */
li {
color: #93a1a1 !important}

/* barva odkazů, odstranění underlined */
a, a:link, a:visited, a:active, a:hover, .link2link, .comma {
text-decoration: none !important;
border: none !important;
outline: none !important;
color: #2aa198 !important}

/* základní významy */
span, div#fastMeanings {
color: #859900 !important}

/* odstranění tučného písma v základních frázích */
span.bold {
font-weight: 400 !important;
color: #b58900 !important}

span.w, span.w.help {
color: #586e75 !important}

/* výslovnost */
.pron {
color: #657b83 !important}

/* ikona výslovnosti */
img[src="/img/ico-speaker.gif"] {
width:0;
height:16px;
padding-right:16px;
background:transparent url("http://cdn1.iconfinder.com/data/icons/Boolean/Electronics/Volume%20Lvl%203.png") top left no-repeat;}

/* gramatika */
.morf {
Font-weight: bold !important;
color: #cb4b16 !important}

/* zarovnání hlavního containeru */
div#container {
margin-top: 0px !important;
margin-left: 0px !important}

/* skrytí konjugace a deklinace*/
a#m-01.morfLink, a#m-00.morfLink {
display: none !important;}

/* skrytí různých elementů */
a#fromCzech,
a#toCzech,
div#line,
div#column,
div#foot,
div.content,
div#menu,
div.superSelect,
input#searchButton.submit,
#add-to-dictionary-btn {
display: none !important;}

/* blokování obrázků */
img[src="/img/slovnik-2011-logo-big.gif"],
img[src="/img/ico-keyboard.gif"],
img[src="/img/slovnik-2011-logo-small.gif"] {
display: none !important;}

}
