css:
@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {



.new_post_label/*_icon*/ > I {display: none !important}

.new_post_label {

  background-clip: content-box;

  padding: 77px 1px 2px 0 !important;

}

.new_post_label:hover {

  color: black !important;

  background-color: rgba(192,192,192,.7) !important;

}

.new_post_buttons {

  background: white center no-repeat url('http://i.imgur.com/iEh44Vp.png') !important;

}



}
