css:
@-moz-document regexp("https://[a-z0-9]+\\.my\\.salesforce\\.com/.*") {
.content h2.pageDescription { font-size: 250% !important; font-weight: 900 !important }

/* remove background color */

body .bPageBlock .pbBody .labelCol { color: #888 }


body .bPageBlock, body #bodyCell .bResource .secondaryPalette, body .secondaryPalette.bPageBlock, body .individualPalette .secondaryPalette.bPageBlock, body .bodyDiv .genericTable, body .genericPageBlockTable, body .bodyDiv .bSubBlock, body .bComponentBlock .bPageBlock, body .bMyDashboard .bPageBlock, body.rlHoverFrame .bPageBlock, body.subjectSelectionPopup div.choicesBox, body.lookupTab .secondaryPalette.bPageBlock, body.popupTab .secondaryPalette.bPageBlock, body.UserTagStatsPage .secondaryPalette.bPageBlock {
background-color: #fff
}


/* remove vestigal borders */
body .bSubBlock .lbBody { border: 0 }

/* make text bigger */
td.dataCol, td.data2Col, td.dataCell, th.dataCell { font-size: 150% }


/* make text fields bigger */

.lookupInput input[type="text"], .requiredInput input[type="text"] { min-width: 275px }
.lookupInput input[type="text"][onfocus], .requiredInput input[type="text"][onfocus] { min-width: 100px }


/* make buttons colored */
#editPage input.btn[title="Cancel"] { background: #FFDEDE }
#editPage input.btn[title="Cancel"]:hover { background: #DFBEBE }
#editPage input.btn[title="Save"] { background: #E0FFAC }
#editPage input.btn[title="Save"]:hover { background: #C0DF8C }
#editPage input.btn[title="Save & New"] { background: #E4FFFF }
#editPage input.btn[title="Save & New"]:hover { background: #C4DFDF }

input.btn[title], input.btn[onclick] { padding-left: 1em !important; padding-right: 1em !important }

th.noRowsHeader { color: #aaa !important ; background: white !important }
}
