css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cnbeta.com"), domain("www.cnbeta.com") {}

@-moz-document url-prefix("http://www.cnbeta.com/articles"), url-prefix("http://cnbeta.com/articles") {

  /* 隐藏不需要的元素 */
  body > *, div.wrapper > *, section.main_content > *, section.main_content_left > *, article.article > *, div.body > *, aside.main_content_right > * { display: none !important; }
  div.wrapper, section.main_content, section.main_content_left, article.article, div.body, div.body > header, div.body > div.content, aside.main_content_right, aside.main_content_right > section.hotcomments, section.article_content { display: block !important; }

  /* 黑底 */
  html { height: 100%; background: #000 !important; opacity: 0.9; }
  body { background: none !important; }
  
  /* 居中 */
  div.wrapper { width: 660px !important; margin: 0 auto !important; }
  section.main_content, section.main_content_left, article.article, div.body { width: 100% !important; }
  
  /* 标题摘要字体 */
  div.title_bar, div.introduction > div { display: none; }
  #news_title { font-size: 24px !important; }
  div.introduction, div.content { font-size: 18px !important; line-height: 1.5em !important; }
  
  /* 保留热门评论 */
  aside.main_content_right { position: fixed; top: 20px; left: 0; width: 200px !important; height: 600px !important; overflow: auto; }
  #J_hotcommt_list { padding: 0 !important; background: #aaa !important; }
  #J_hotcommt_list li { width: 170px !important; padding: 5px !important; font-size: 15px !important; border-color: #888 !important; background: #aaa !important; }
  header.yellow_bar, div.yellow_bottom_v2, div.re_mark, div.comContent > p.title { display: none !important; }
  div.comContent { padding: 0 !important; }
  
  /* 简单去广告 */
  div.content div[id^=googleAd], div.content div[class^=gbox] { display: none !important; }
   
}
