css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");
/* This style only to be used with fxOpera "complete theme" available at https://addons.mozilla.org/en-US/firefox/addon/fxopera/?src=api */

#main-window[sizemode="normal"] #appmenu-button-container, 
#main-window[sizemode="maximized"] #appmenu-button-container, 
#main-window[privatebrowsingmode] #appmenu-button-container {
    background-clip: padding-box !important;
    position: relative !important;
    margin: 0 !important;
    padding: 0 !important;
}

#main-window[sizemode="maximized"] #appmenu-button {
    
}

#TabsToolbar{
    max-height: 28px !important;
    margin: 0px !important;
    margin-top: -7px !important;
}

#main-window[tabsontop="false"] #nav-bar{
    margin-top: 5px !important;
}

#appmenu-button-container {
    background: none !important;
}

#appmenu-button, #main-window[privatebrowsingmode] #appmenu-button {
    margin-top: 6px !important;
    background-color: rgba(255,255,255,0.5) !important;
    background-image: url("chrome://branding/content/icon48.png") !important;
	background-repeat: no-repeat !important;
	background-position: top !important;
	background-size: 80% !important;
	box-shadow: none !important;
    border: 1px solid rgba(146,146,146,0.75) !important;
    border-bottom: 0 !important;
    border-radius: 5px 0 0 0 !important;
	height: 28px !important;
}

#main-window[sizemode="maximized"] #appmenu-button {
    margin-top: 2px !important;
    border-radius: 0 !important;
}

#main-window[privatebrowsingmode] #appmenu-button {
    background-color: rgba(70,0,198,0.4) !important;
    border-color: #20005C !important;
}

#appmenu-button:hover, #main-window[privatebrowsingmode] #appmenu-button:hover {
    background-color: rgba(255,255,255,0.7) !important;
}

#appmenu-button .button-text, #appmenu-button .button-menu-dropmarker, #appmenu-button .button-icon, #appmenu-button .button-box {
	visibility: hidden !important;
    width: 0 !important;
}
