css:
@-moz-document domain("webdesignrecipes.com") {

  nav, #searchform, #ad, .single-adsense,
  #article-footer-social, #related-posts, #comment-open,
  #trackback-list, #respond, footer
  { display: none !important; }


  #container {
    width: 700px;
    margin: 2em auto !important;
    padding: 4em !important;
    background: rgba(255,255,255,0.3);
    box-shadow: 0 0 5px #ddd;
  }

  #article-header, .col-min, .col-max {
    margin-left: 0 !important;
    width: 700px;
  }

  #article-header>h2 { margin-top: 1em; }
  #article-header>h2 a { color: #555; background: none; }

  .comment-warrper {
    margin-left: 0 !important;
    width: inherit !important;
  }

  .comment-body {
    width: 70%;
  }

}
