css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("spiegel.de") {

div.column-wide {
	width:auto !important;
	background:none !important;
}

div.column-small {
	display:none !important;
}

div.pano_xxl,
div.ressort-teaser-box-body {
	background:none !important;
}

.commonpager .commonpager-indicator {
	width:610px;
	text-align:center;
}

.commonpager .commonpager-indicator span {
	line-height:4.8rem !important;
	margin:0 1em !important;
}

div.breadcrumb-social,
div.top-poster,
div.wetterboxhp,
div.boersenboxhp,
div.hpheftkasten,
div.ressort-teaser-box,
div.commonpager.mumelei-5-pics.asset-box.inline-pic-credit {
	display:none !important; 
}

#footer {
	display:none !important;
}

}
