css:
@-moz-document domain("feedly.com") {
#feedlyFrame { width : 100% !important;}

/*共有バーの幅*/
#sideArea, #systemBar, .bottomWikiWidget {
  display: none !important; 
}

/*サイドバーの幅*/
#mainBar{
margin-left: -100px !important; 
width :auto !important; 
}

/*サイドバーのレイアウト1*/
#feedlyTabs{
 width: 185px !important;
 padding-top: 0px !important;
 padding-right: 0px !important;
 padding-left: 0px !important;
 height:100% !important; 
 overflow-y: auto !important;
}

/*サイドバーのレイアウト2*/
#feedlyTabsHolder{ 
  margin-top:0px !important; 
  height:100% !important; 
  line-height: 15px !important; 
  width: 190px !important;
}


#feedlyTabsHolder div.simpleUnreadCount {
width: 24px  !important; 
float: right  !important; 
padding-right: 20px  !important; 
padding-left: 0px  !important; 
}

#feedlyPage, #mainArea {
  width:90% !important;

}
#feedlyPart {
  width : 98% !important;
}


#feedlyPart0.area {
  margin-top: 0px  !important; 
  padding-left: 15px  !important; 
  padding-top: 0px  !important; 
  padding-right: 1px  !important; 
}


.entryHolder {
width:100% !important;
max-width: 100% !important;
margin:0px !important;
}
.entryBody {
max-width: 100% !important;
  margin:0px !important;
}
.entryHeader{
max-width: 100% !important;
  margin:0px !important;
}

.content img { margin-left:0px !important;  }

.u100Entry iframe { margin-left: 0 !important; }

.u100Entry .title { 
  font-size: 100%  !important;
  font-weight: bold !important;
  font-family:arial,sans-serif !important;
  max-width: 100% !important;
  line-height: 25px !important;
  padding:1px !important; 
  }

.u100frame.selectedEntry{
  padding:1px !important; 
  box-shadow: 3px 3px 3px 3px rgba(0, 0, 0, 0.4) !important;
 }

.u100frame{
  padding:1px !important; 
  border: 5px solid rgb(255, 255, 255) ;
  box-shadow: 3px 3px 3px 3px rgba(17, 104, 13, 0.4) !important;
  margin-top: 10px !important;
  margin-bottom: 10px !important;
  }

  
.u100frame table{ padding:0px !important; }

.u100Entry {
  padding:1px !important; 
  width: 100% !important;
}

.u100Entry.selectedEntry {
  padding:1px !important; 
  border: 10px solid rgba(17, 104, 13, 0.4) !important;
  width: 100% !important; 
}


element.style {
  text-align: left  !important; 
  padding: 100px !important; 
  margin-left: 0px  !important; 
  width: 77px  !important; 
}

.area { margin-left:0 !important; } 

.entryBody{
  margin: 0px!important;
}



#mainArea .u10Entry 
 { max-width:none !important; }


#floatingBar
 { width: 73% !important;}

#recommentationAreaHeadlines>div { width:28% !important; }
#recommentationAreaHeadlines .topRecommendedEntry { width: 100% !important; }

BODY.home { background: #FFFFFF  !important;  }
}
