css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp('http://www\\.neogaf\\.(com|net)/.*')
{          
    body
    {
        font-size: 12px !important; 
    }
    
    #forum-stats
    {
        font-size: 12px !important;    
    }
    
    #posts
    {
         border: 1px solid #474747 !important;   
    }
    
    .thead, .thead a
    {
        text-align: left !important;
        margin-left: -5px !important;
    }

    .threadbit td
    {
        text-align: left !important;
        font-size: 12px !important;
    }
    
    .threadbit td a
    {
        font-size: 12px !important;
    }
    
    .threadbit td a:visited
    {
        color: #6B7993 !important;
    }
    
    .threadbit td a:visited:hover
    {
        color: #FF9900 !important;
    }
    
    .lastposticon-threadbit
    {
        margin-top: -3.5px !important;
    }
    
    #headernav > li > a
    {
        font-size: 12px !important;
    }
    
    #headeruser > .headeruserpm, #headeruser > .headeruserbit > a
    {
        font-size: 12px !important;
    }
    
    .tborder
    {
        border: 1px solid #474747 !important;
    }
    
    .post
    {
        font-size: 12px !important;
    }
    
    .threadbit-title 
    {
        font-size: 12px !important;
    }
    
    .tcat
    {   
        font-size: 12px !important;
    }
    
    .thread-prefix
    {
        background: #01518E !important;
        color: #FFFFFF !important;
        padding: 2px !important;
    }
    
    .large-button, a.large-button
    {
        font-size: 12px !important;
    }
    
    .forum-tabs > li > a
    {
        font-size: 12px !important;
    }
    
    .pagenav > li
    {
        font-size: 12px !important;
    }
    
    .postbit-details
    {    
        font-size: 12px !important;
    }
    
    td, th 
    {
        line-height: 104% !important;
    }
    
    .usercp-sidebar > h3
    {
        font-size: 12px !important;
    }
    
    .usercp-sidebar > dl > dt
    {
        font-size: 12px !important;    
    }
    
    .usercp-sidebar > dl > dd a
    {
        font-size: 12px !important;    
    }
    
    .postbit-post .cite
    {
        border-bottom: 1px solid #cfcfcf !important;
    }
    
    .multiquotelink
    {
        margin-right: -3px !important;
    }
    
    .post-links
    {
        margin-top: 1px !important;    
    }
    
    .forum-info > h3
    {
        font-size: 12px !important;
    }
    
    .forum-info-subforums a
    {
        font-size: 12px !important;
    }
    
    .forum-bit-lastpost strong
    {
        font-size: 12px !important;
    }
    
    .dropdown > li > a
    {
        font-size: 12px !important;    
    }
    
    input, button, textarea, select
    {
        font-size: 12px !important;      
    }
}
