css:
@-moz-document url-prefix("http://techreport.com") {
#insidepage > p {
    font-size: 125%;
    line-height: 130%;
}

#insidepage > .subhead, #insidepage > .headline {
    font-size: 180%;
    line-height: 2.75em;
}

#insidepage blockquote {
    font-style: italic;
}

#insidepage li {
    margin-bottom: 0.5em;
}

#insidepage p.caption {
    font-size: 80%;
}

#metal .right {
    font-size: 125%;
}
}
