css:
@-moz-document domain("hypem.com") {
div#menu-friends
    {
        display: none !important;
    }

	li#menu-item-featured
	{
        display: none
    }

	li#menu-item-directory
	{
        display: none
    }

	li#menu-item-labs
	{
        display: none
    }


    div#popular
    {
      display: none
    }

	div.share-icons
	{
        display: none
    }

    
	div.section-track
	{
        padding: 5px 0px 10px 0px !important;
        min-height: 90px;
    }


    div.track-info
	{
        float:right;
        margin-right:15px!important;
    }


    div.section-player
    {
        margin-top:30px!important;
    }


    div.section-player > a.thumb
	{
        display: none !important;
    }


	div.section-player > p
	{
		display: none;
	}


	h3.track_name,
	div.meta
	{
	   position: relative;
	   left: -55px;
	   width: 550px;
	}


    span.rank
    {
        position: relative !important;
        float: left;
        left: -55px!important;
        padding: 5px 0 !important;
        text-align: center !important;
        width: 32px;
        margin: 0 15px 30px 0 !important;
    }


    span.share-links
	{
        display: none !important;
    }
    

    a.sparkdiv
	{
        position: static !important;
        float: right;
        margin:35px -107px 0 0 !important;
    }


	span.buy > span.download
	{
		display: none;
	}
	

    div.meta
    {
        border-bottom: none !important;
    }


	div.favcountlist 
	{
		display: none !important;
	}


    div.section-track.same
	{
        border-top: 1px dotted rgb(221, 221, 221) !important;
    }


    div.fast-forward-launch
    {
        margin-top: 94px !important;
    }


	div#search-tip
	{
		overflow: hidden;
	}

    
	div#ad-breakout
    {
        display: none;
    }

	div#ad-leaderboard1
    {
        display: none;
    }

	div#ad-leaderboard2
    {
        display: none;
    }

	div#content-right
    {
        display: none;
    }

	form#ad-report
	{
		display: none;
	}

	div#ad-leaderboard3
    {
        display: none;
    }
}
