css:
@-moz-document domain("i-mezzo.net") {

  #bar, #category_index, #indivisual_left,
  .blog>div:first-child, .sbver, .previous-body,
  *[class*="comments"], #zenback, #return, #foot
  { display: none !important; }

  #banner, #wrap {
    width: 615px !important;
    margin: 2em auto;
  }
  #indivisual_main {
    float: none;
    background: none;
    padding: 20px;
    box-shadow: 0 1px 4px #ddd;
    border-radius: 3px;
  }
  #banner {
    box-shadow: 0 1px 4px #ddd;
    border-radius: 3px;
  }
  body {
    background-color: #f4f4f4;
  }
  .date {
    color: #666; margin-bottom: 5px;
  }
  h1 {
    color: #444 !important;
  }
  .entryurl {
    display: block;
    padding: 0.5em;
    text-align: left;
    font-size: 16px;
  }
  .blogbody_individual {
    line-height: 1.9;
  }

}
