css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("muzebra.com") {
		
	*[href*="donate"], *[class*="premium"],  #aside-banner, #yw1{
    	display: none !important;
        background-image: none !important;
        background-color: hsl(60, 80%, 50%) !important;
        color: red !important;
	}
	
	body {
		background-image: none !important;
	}

	body, body > * {
		background-color: hsl(211, 80%, 25%) !important;      
	}
    
	body * {
		color: hsl(211, 60%, 80%) !important;
		text-shadow: 0.3px 0.5px 0.3px hsl(211, 80%, 25%) !important;
	}

	ul.playlist li {
		background-image: linear-gradient(to right, hsl(211, 60%, 25%), hsl(211, 80%, 30%)) !important;
	}

	ul.playlist li .playlist_button {
		box-shadow: 10px 0px 10px hsla(211, 100%, 60%, 0.3) !important;
	}

	div.player_wrap * {
		color: hsl(211, 80%, 25%) !important;
	}

	input, input *, div.hash, div.hash *, .header, .stat {
		color: hsl(211, 80%, 25%) !important;
		background-image: none !important;
		background-color: hsl(211, 60%, 80%) !important;
	}
}
