css:
@-moz-document domain("reader.aol.com") {
.article-body img {
    float: none!important;
    margin: 0;
    width: 500px!important;
    display:block!important
}
.reader-ad-container, .content img, .content embed, .content object, .article-body img.img-ad {display:none!important}

#reader-container { margin-right: 0px!important}
.feed-header { right: 0px!important}

.article-item-view .article-content { max-width: 1100px !important}

.article-body a[target='_blank'] img{min-width:600px}
.article-body a[target='_blank']{display:block; width:600px}
 
}
