css:
@-moz-document url-prefix("http://blog.likelikeslike.com/"), domain("likelikeslike.com") {
body{
	background-image: -webkit-linear-gradient(top, rgba(255, 255, 255, 0.298039), rgba(255, 255, 255, 		0.298039)),url(http://img13.poco.cn/mypoco/myphoto/20121210/22/632086652012121022063907.png)!important;
  	background-image: -moz-linear-gradient(top, rgba(255, 255, 255, 0.298039), rgba(255, 255, 255, 		0.298039)), url(http://img13.poco.cn/mypoco/myphoto/20121210/22/632086652012121022063907.png)!important;
    background-size: cover !important;
	background-attachment: fixed !important;
	background-position: 50% 50% !important;
	background-repeat: no-repeat no-repeat !important;
}
#main,#categories-nav a,article blockquote.citation,article pre.prettyprint,article .contents,article .contents:hover,.post-title,p.post-footer{
  	background-color: rgba(250, 250, 250, 0.49) !important;
	background-image:none !important;
}
#main,article .contents{
  	border: 2px solid rgba(221, 221, 221, 0.48) !important;
}
.textarea-wrapper{
  	background: rgba(255, 255, 255, 0.56) !important;
}
input[type="text"]{
  	background-color: rgba(255, 255, 255, 0.51) !important;
	border: 2px solid rgba(221, 221, 221, 0.48) !important;
}
}

@-moz-document url-prefix("http://tmp.likelikeslike.com/") {
#header,.post-body h2,#footer{
  	background: rgba(68, 85, 102, 0.6) !important;
}
.post-body .warning,.hicode,.linenodiv,.post-body .pull-quote{
    background-color: rgba(250, 250, 250, 0.49) !important;
}
}

@-moz-document domain("disqus.com") {
.textarea-wrapper,.input-wrapper input,.postbox .post-actions{
  	background: rgba(255, 255, 255, 0.6) !important;
}
.postbox .post-actions{
  background: rgba(246, 248, 249, 0.6) !important;
}
}
