css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("doom9.org") {

input
{
background: #000000 !important;
}
html, .page, td, .tborder
{
background: #111111 !important;
}
.alt1
{
background: #101010 !important;
}
.alt2, .alt2Active
{
background: #222222 !important;
}
.thead, .tcat, #threadtools, #threadsearch,
#displaymodes
{
background: #333333 !important;
}
.thead, .tborder
{
border-color: #333333 !important;
}
hr
{
border-color: #CCCCCC !important;
}
.alt1
{
border-color: #101010 !important;
}
.alt2
{
border: 1px #222222 solid !important;
}
.tborder .alt1Active, .tborder .alt1
{
border-bottom: 3px #222222 solid !important;
}
input
{
border: 1px #222222 solid !important;
}
a
{
text-decoration: underline !important;
}
a
{
color: orange !important;
}
a:hover
{
color: #DD5608 !important;
}
.alt2, .alt2Active, .smallfont, center,
.alt1, .alt2, .thead, .tcat, input
{
color:#CCCCCC !important;
}
}
