css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);


/* Fix forward button */



#forward-button {
-moz-appearance: none !important ;
-height: 27px !important ;
list-style-image: url("http://i.imgur.com/ZdQby19.png") !important;
-moz-image-region: rect(2px, 424px, 33px, 396px) !important ; 
}

#forward-button:hover {-moz-image-region: rect(36px, 424px, 67px, 396px) !important ; }

#forward-button:hover:active {-moz-image-region: rect(104px, 424px, 135px, 396px) !important ; }

#forward-button[disabled="true"], #forward-button[disabled="true"]:hover:active {-moz-image-region: rect(70px, 424px, 101px, 396px) !important ; }



/* Fix back-forward dropmarker */



#back-forward-dropmarker {
-moz-appearance: none !important ;
height: 27px !important ;
margin-left: -4px !important;
list-style-image: url("http://i.imgur.com/ZdQby19.png") !important;
-moz-image-region: rect(2px, 438px, 33px, 424px) !important ; 
}

#back-forward-dropmarker:hover {-moz-image-region: rect(36px, 438px, 67px, 424px) !important ; }

#back-forward-dropmarker:hover:active {-moz-image-region: rect(104px, 438px, 135px, 424px) !important ; }

#back-forward-dropmarker[disabled="true"], #back-forward-dropmarker[disabled="true"]:hover:active {
opacity: 1 !important ;
-moz-image-region: rect(70px, 438px, 101px, 424px) !important ; 
}
