@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("washingtonpost.com") {

.end, #weather-widget,
.centerArea > div:nth-child(3), #footer,
#carousel-full, #social-tools, .subsection,
.ten > div:nth-child(10), #echo_container_a,
div.module:nth-child(7), #article-side-rail,
div.module:nth-child(18), 
#mod-m1-2-1584415f-88e9-4c85-9f8d-8137a403337d {
    display: none !important;
    }
#utility-wrapper {
    margin-top: -25px !important;
    }
#global-signin {
    background: red !important;
    padding: 5px !important;
    margin-top: 50px !important;
    margin-left: -10px !important;
    position: absolute !important;
    border: 1px dotted #ccc !important;
    }
#header-v3 #utility-wrapper {
    overflow: visible !important;
    }
.vertical-rule-2-to-3, .v-rule-4-6 {
    background:  none !important;
    }
#masthead-v4.isHome {
    height: 80px !important;
    }
.wp-column.ten {
    width: 990px !important;
    }
.wp-column.four-mini {
    width: 425px !important;
    }
.wp-column.six {
    width: 540px !important;
    }
.wp-column.five {
    width: 490px !important;
    }
.module.p1-1d div.container {
    width: 475px !important;
    }
.module.s5 div.container {
    width: 235px !important;
    }
.wp-column.two-half {
    width: 220px !important;
    }
#article-leaf-page-footer-taboola .tabbed-article .container {
    width: 300px !important;
    }
#article_body {
    width: 700px !important;
    }
p {
    font-size: 1.8em !important;
    line-height: 1.5em !important;
    color: #6b6b6b !important;
    }
.module p  {
    line-height: 1.3 !important;
    }
#article .article_body p {
    font-size: 2.2em !important;
    line-height: 1.4 !important;
    }
ul.normal li, ol.normal li {
    font-size: 1.6em !important;
    }
h2, h3 {
    font-size: 1.7em !important;
    }

}
