css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.netvibes.com/privatepage/") {

/* change font size from 11 to 13px*/
body {
    font: 1.3em/1.3em Arial,Tahoma,Verdana,Helvetica,"Bitstream Vera Sans",sans-serif !important;
}
.nv-feedList li {
    line-height: 1.3em !important;
}

/* remove some white space above top gadgets*/
#divTabsOuter {
    display: none !important;
}
#columns {
    padding-top: 0 !important;
}

/* hide pagers and unread counts */
.nv-pager, .counter {
    display: none !important;
}

/* hide tabs selector and column resize grippers */
#divTabs, .gripper {
    visibility: hidden !important;
}

/* hide annoying red dot */
#savingStatus {
  visibility: hidden !important;
}

}
