css:
@-moz-document domain("altcha.in"), domain("explorer.doubloons.net"), domain("cryptocoinexplorer.com") {
body, #header, table {
  background: #222  fixed !important;
  color: #EEE;
  font-family: consolas;
}

h1 {
  color: green;
}

td, th {
  font-family: consolas;
}

a:link, a:visited {
	color: #0BF !important;
}

table, h1, h3, p {
  width: 1100px;
  margin: 5px auto
}
h1, h3, p {
  text-align: center;
}

#header h1 {
  width: 100%;
  color: green;
}

}

@-moz-document domain("cryptocoinexplorer.com") {
#header h1 {
  width: 100%;
}

table { 
  border: 1px solid #ccc;
}

table, tbody, tr, th, td {
  border-color: #ccc !important;
}
}
