css:
@-moz-document domain(inoreader.com) {
body {
font-family: tahoma!important;
unicode-bidi:embed!important;
}
.parent_div{font-family: arial,tahoma,verdana,helvetica;}
.article_content {
font-family: "tahoma",tahoma !important;font-size:13px;
}
.article_feed_title{
padding-top:15px;
  }

.unread_cnt { font-size: 13px; color: #007ff6; font-weight: bold; }
.selected { color:#364066; background-color: #dfe1e8;border: 1px solid #cbcfda; }
.article_card { border-radius:4px !important; box-shadow: 4px 4px 4px #007ff6; margin-bottom: 20px !important; }
.article_footer { border-left: 4px solid #ddd; margin-left: -5px;}
.article_unreaded { border-color: #c5c3cd; }
.article { border:#ece6e6 1px solid }
}
