css:
@-moz-document url-prefix(http://theoldreader.com/) {
body {
	font-family: 'Iranian Sans';
    font-size:14px;
    line-height:18px;
}

.post.listview .header .span8 {
   direction: rtl;
   text-align: right;
   min-height: 30%;
   width: 76%;
   float:right;
  }
        
        
.reader .sidebar-fixed-top {
             width:13% 
            }
            
.reader .body-fixed-top {
                 left:11%; 
                 width:86%
                }

.post.listview .header .name{
  float:right;
        }

.row-fluid [class*="span"]:first-child {
                     width:13% 
                    }
        
        .post.unread {
              border-left: 5px solid #FA5882;
}

.post.listview .header .name{
margin-left:22px;
        }

.post .content 
{
  direction:rtl;
  text-align:right;
}

.home-page dl dd {
  margin-left:0px;
  }

.post.unread:hover{
       color:#8A0829;
       border: 1px solid #8A0829;
      }
.post.unread{
       border: 1px solid #FFFFFF;
      }

}
