css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.politico.com") {

.trending-topics, #secondaryContent, #footer,
#comments, .tangential-wrapper, .story-embed,
div.story-toolbar:nth-child(6), .follow-authors,
.tags, .prompt, .highlight-box-footer,
#highlightScoreboard, #subscribeHuddle,
div.story-toolbar:nth-child(7), .section-head {
    display: none !important;
    }
body {
    background: #fff !important;
    }
#siteWrapper  {
    border: none !important;
    }
.primary-masthead {
    padding: 1px 15px !important;
    }
#mainContent {
    width: 999px !important;
    }
#live-events #mainContent {
    width: 970px !important;
    }
.t1-sub {
    width: 970px !important;
    }
.post, .article {
    width: 700px !important;
    }
.t1-story .t1-sub li {
    margin-right: 190px !important;
    }
.topic-utilities {
    width: 970px !important;
    }
.t1-story .t1-sub li.last {
    float: right !important;
    margin-right: 0 !important;
    }
.main-stories-double .main-stories-list {
    width: 470px !important;
    }
p {
    font-size: 1.5em !important;
    line-height: 1.1 !important;
    color: #676767 !important;
    }
.main-stories .related li, ul.polls-list li div {
    font-size: 1.2em !important;
    }
.p-content p {
    color: #676767 !important;
    font-size: 2.0em !important;
    line-height: 1.2 !important;
    margin-bottom: 0.9em;
    }

.main-stories .de-emphasized h3, ul.polls-list h3  {
    font-size: 1.7em !important;
    margin-bottom: 10px !important;
    }
.highlight-box .story-list h3, .supplementary-links li {
    font-size: 1.5em !important;
    }
.supplementary-links-internal, .supplementary-links-external {
    width: 470px !important;
    }




}
