css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("soundcloud.com") {
.whoToFollowModule,
.statsBadge__upsell, 
.dashbox,
.l-footer, 
[class*="appStoreButto"]
{display: none !important;}

.userInfo__actions-fixed, 
[id*="overlay"],
body,
.listenContent__inner,
.sound__soundActions,
.search__headerInner,
.userNetworkTop__inner,
.soundBadge
{background-color: #00000f !important;}

p, [class*="userBadge"]
{color: rgb(204, 204, 204) !important;}

.sc-ministats{color: #00FF00 !important; 
             font-size: 11pt !important;}
.relativeTime{color: deeppink    !important;}   
.sc-link-dark,
.soundTitle__title {color: pink !important;}

.sc-link-light {color: orange !important;}
.sc-media-content{font-size: 13pt !important;}
.sound__footer {height: 12px !important;}
.sc-truncate {font-size: 70% !important;}
}
