css:
@namespace url(http://www.w3.org/1999/xhtml);
/* ============== Главная страница ============== */ @-moz-document domain("mob.ua") {
div.mob-rec_sm + div.gm-top-image ,div.mob-rec_sm /* Подсадные утки в рекомендованных играх слева (платные игры) */
,div.panel-genre:nth-child(2)>ul:nth-child(3)>li:nth-child(8) /* Рекомендуемые игры сбоку (Android) */
,div[style="width: 728px; height: 90px;"] ,#banner-w790 ,#banner-w468 ,#banner-h400 /* Баннеры */
,dl.menu-genres:nth-child(2)>dd:nth-child(10) /* Рекомендуемые игры сбоку */
,#content_header /* "Скачать игры для мобильных телефонов бесплатно" */
,td.top_line:nth-child(5) /* Продажа рекламы (табличная вёрстка) */
,.gbb /* Подсадные уытки на странице (платные игры) */
,#lay-panel-left>div:nth-child(5) /* Реклама слева */
,.users_top10 ,.social_block  /* Лишка справа */
,#body_footer ,.new_bottom_tbl ,.ny_footer /* Блок снизу */
,#colorizer-wrapper /* "Улучшение сайта" */
,#social-wrapper /* Соц. сети */
,.ads /* Продажа рекламы */

{display:none!important}

/* Уменьшение длины страницы */
[style="margin: 0px 0px 20px;"] ,[style="margin:0 0 20px 0;"] ,.game-item-outer {margin:0 !important}
div.b-statistic {position:absolute !important; width:350px; left:280px; top:50px}
.l-java {position:absolute; left:200px; top:50px;}
#content_container {margin-top:0px !important}
.j-list {padding:0 !important}

div.pagination {left:-3px; position:relative} /* Небольшое смещение блока со страницами */
.bt2 {padding:6px 30px !important} /* Кнопка "Скачать" стала больше! */

[id^="pgzp_page"] .lay-content .b-silver-panel
,[id^="pgzp_page"] .btn-list-add-game
,[id^="pgzp_page"] #lay-panel-left
,[id^="pgzp_page"] #sidebar_right
,[id^="pgzp_page"] #body_header
,[id^="pgzp_page"] .pagination
,[id^="pgzp_page"] .b-sort
{display:none !important}

}

/* ============== Детальное рассмотрение игры ============== */ @-moz-document url-prefix(http://games.mob.ua/game) {
#lay-panel-left>div:nth-child(3) /* Реклама слева */
,.addthis_toolbox /* Соц. сети снизу */
,.gpb-list /* Рекомендации справа */
{display:none !important}
}

/* ============== Детальное рассмотрение sis-игры ============== */ @-moz-document url-prefix(http://sis.mob.ua/game) {
.gpb-list {display:none !important} /* Рекомендации справа */
}

/* ============== Загрузка ============== */ @-moz-document url-prefix(http://games.mob.ua/download/) {
.tbl-tr-help /* Инструкции (надеюсь, они Вам не понадобятся) */
,[style="margin: 10px 0 0 -3px "] /* Powered by Get4mobile */
,#lay-panel-left>div:nth-child(3) /* Реклама слева */
,.tbl-user-info /* Пользователь, заливший файл */
,.complaint-box /* Жалобы */
{display:none !important}

.bt3 {width:300px !important; height:40px !important} /* Бо-ольшая кнопка загрузки */
}
/* ============== Загрузка sis-игры ============== */ @-moz-document url-prefix(http://sis.mob.ua/download/) {
.gd-help-install /* Инструкции (надеюсь, они Вам не понадобятся) */
,[style="margin: 10px 0 0 -3px "] /* Powered by Get4mobile */
,.complaint-box /* Жалобы */
{display:none !important}

.bt3 {width:300px !important; height:40px !important} /* Бо-ольшая кнопка загрузки */
}
