css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {
    #blueBarHolder #blueBar, .fbChatSidebar, .timelineReportContainer, .timelineUnitContainer {
        background-color: rgba(4,4,4,0.9) !important;
        background-image: none !important;
        box-shadow: 0 0 0.3em 0 #ccc !important;
    }
    
    .fbChatSidebar, .timelineReportContainer, .timelineUnitContainer {
        background-color: #dbdcdf !important;
    }
    
    .pic, .profilePhoto, .uiScaledImageContainer {
        box-shadow: 0 0 0.2em 0 #111, inset 0 0 0.3em 0 #000 !important;
    }
    
    img:not([alt="Mobile"]), .uiScaledImageContainer {
        border-radius: 0.5em !important
    }
    
    .fbNubFlyoutTitlebar, .fbNubFlyoutTitlebar:hover {
        background: #050505 !important
    }
    
    .focusedTab .fbNubFlyoutTitlebar {
        background: #333 !important
    }
}
