css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.gamevn.com") {
  body {
    width: 80% !important;
    margin-left: 10% !important;
  }
  .threadbit { width: 100% !important; }
  .threadbit .threadinfo { width: 70% !important; }
  .threadbit .threadstats { width: 12% !important; }
  .threadbit .threadlastpost { width: 16% !important; }
  
  html { background: #111 !important; }
  .body_wrapper {
    background: #eee !important;
    padding: 5px 10px !important;
    border-radius: 3px 3px 3px 3px !important;
  }
  #header {
    background: #e1e1e2 !important;
    border-radius: 0px 0px 3px 3px !important;
  }
  #header .toplinks { margin-top: 7px !important; }
  #header .toplinks .welcomelink { color: #e1e1e2 !important; }
  .navbar {
    border-bottom-left-radius: 3px !important;
    border-bottom-right-radius: 3px !important;
  }
  
  .announcements .announcerow .announcement font { font-size: 0px; }
  #fGVNAds { display: none !important; }
  .gvn-top-ads { display: none !important; }
  #ft .yui-footer { display: none !important; }
  .gvn-right-ads-1 { display: none !important; }
  .gvn-right-ads-2 { display: none !important; }
  .gvn-right-ads-3 { display: none !important; }
  .gvn-right-ads-4 { display: none !important; }
  .gvn-right-ads-5 { display: none !important; }
}
