css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("inoreader.com") {
    html, body {
        background: #333 !important;
        color: #bbb !important;
    }
    #tree_pane {
        background: #333 !important;
        color: #aaa !important;
    }
    .article, .article_full_contents {
        background: #444 !important;
        color: #f2efdf !important;
    }
    .article_current {
        background: #555 !important;
        color: #f2efdf !important;
    }
    a.bluelink:link, #view_toolbar div:nth-child(2) > span {
        color: #4eb1c6 !important;
    }
    .article_author > span, .normal.tree_unreaded, .selected, .article_unreaded {
        color: #ddd !important;
    }
    .unread_cnt {
        color: #aaa !important;
    }
    .parent_div_inner:hover {
        background: #444 !important;
    }
    .selected_parent {
        background: #222 !important;
    }
    button, #subscriptions_button {
        background: #555 !important;
        color: #aaa !important;
        border: 1px solid #222 !important;
    }
    button:disabled {
        background: #333 !important;
        color: #888 !important;
    }
}
