css:
@-moz-document url-prefix("http://www.myth-weavers.com/sheetview.php") {
#maintable > tbody > tr #main {
  	width: 80% !important;
}

html,body{
	height: 100% !important;
  	overflow-x: hidden !important;
  	margin: 0 auto !important;
  	padding: 0 !important;
}

html {
 	width: 100% !important;
  	overflow-y: hidden !important;
}

body {
  	width: 100% !important;
	overflow-y: auto !important;
}

#toolBar {
 	position: relative !important; 
  	margin-bottom: 0 !important;
}

#skillcontainer {
	width: 35% !important;
}

#maintable > tbody > tr td {
	
}

.skillslot > .skillmod:nth-child(4) > .text {
	font-weight: bold !important;
    background-color: #DDD !important;
}

.skillslot > .skillmod:nth-child(8) > .text {
	font-weight: bold !important;
    background-color: #F1F1F1 !important;
}

.name > input[style*="italic"] {
  	font-weight: bold !important;
}

.footer td {
	background-color: #EEE !important;
    font-weight: bold !important;
  	color: black !important;
}

#footer {
  	margin-bottom: 40px !important;
}

#save {
 	position: absolute !important;
  	bottom: 0px !important;
  	padding: 3px 0 !important;
  	background-color: rgba(0, 0, 0, 0.5) !important;
  	width: 78.25% !important;
}

.skillslot:nth-child(even) * {
  background: #CCC !important;
}
    
[class*="slot"] input {
  border-top: none !important;
}
    
#gear tr:nth-child(even) * {
  background: #CCC !important;
}
    
#feats tr td:nth-child(even) * {
  background: #CCC !important;
}
    
#maintable #info {
  border: 1px solid black !important;
  padding: 10px 5px !important;
}

#languages tr:nth-child(even) * {
 background-color: #CCC !important; 
}

.spelllist tr:nth-child(even) * {
  background-color: #CCC !important;
}
}
