css:
@-moz-document domain("lifehacker.com") {
.hide-for-small
{
  display: none !important;
}

.post-content .first-text
{
  font-style: italic;
}

.eight, .row .eight, .ten, .row .ten
{
  width: 100%;
}

.push-two 
{
  left: 0;
}

.six, .row .six 
{
  width: 40%;
}

div[class="six columns end"]
{
  width: 65% !important;
}

.has-media.media-300, .has-media.media-470 {
width: 300px !important;
}

.post-content p {
text-align: justify;
}
}
