css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.thedailybeast.com") {

.login-search-nav, #partners, #pageFooter, .outbrain,
.storymeta, .comments {
    display: none !important;
    }
.heading-style-a, .story p, #home-page .featurePicks li, .summary,
.newsletter .description, .sidebar-style-b  {
    font-size: 16px !important;
    line-height: 1.4 !important;
    
    }
#article-page p, .stream-item p {
    font-size: 1.7em !important;
    line-height: 1.2 !important;
    color: #787878 !important;
    }

.features .scroll, .features .scroll .feature {
    height: 550px !important;
    }
}
