css:
@-moz-document domain("stackoverflow.com") {
.badge { border: 1px solid rgba(0,0,0,0); box-shadow: 1px 1px 2px 0 rgba(0,0,0,0.7); }
.badge[title^="bronze"], .badge3 + .badgecount { background: #cc9966; }
.badge[title^="silver"], .badge2 + .badgecount { background: #c5c5c5; }
.badge[title^="gold"], .badge1 + .badgecount { background: #ffcc46; }
.badge1, .badge2, .badge3 { display: none; }
.badge,
.badge1 + .badgecount, 
.badge2 + .badgecount, 
.badge3 + .badgecount
{ text-shadow: 0 0 2px black; color: white; }

.badge1 + .badgecount, 
.badge2 + .badgecount, 
.badge3 + .badgecount
{ padding: 1px 4px; margin: 0 1px; border-radius: 5px; -webkit-border-radius: 5px; -moz-border-radius: 5px;
  box-shadow: 1px 1px 2px 0 rgba(0,0,0,0.4); }
}
