css:
@-moz-document url-prefix("http://droider.ru/post/") {
.right, #footer, .furjbqy, .date, .singleinfo, .entry h4[style="margin: 10px 0px 5px 0px;"], 
.entry div[style="border: 0pt none ; margin: 0pt; padding: 0pt;"] a, img[title="Google Maps"]
{
  display: none !important;
}

#content 
{
  width: 100%;
}

.single 
{
  margin: 0 15px 0 0;
  width: 97%;
}

#casing 
{
  background-color: #FFFFFF;
  background-image: none;
  width: 1100px;
  float: left;
  
}

#wrapper
{
  width: 1100px;
}

#top 
{
  width: 1100px;
}

.entry p
{
  text-align: justify;
}

#comment 
{
  width: 97%;
}

#commentform textarea 
{
  width: 99%;
}

#s 
{
  width: 189px;
}
}
