css:
@-moz-document domain("gihyo.jp") {
.utility, #categoryNavigation, #topicPath,
#secondary, #sub, #footer, #comment>.sectionBlock03,
.readingTime,
.readingTime+.pageSwitch01,
*[id*="ads"],
*[id*="anner"],
*[id*="AdSense"]
{ display: none !important; }

#header { height: 115px; background: none; }
#content { margin: 0 0 5em; }
#wrapper { width: 800px !important; }
#main { width: 100% !important; }
#primary { width: auto !important; max-width: 100%; }

body { font-family: sans-serif !important; }
code { font-family: monospace !important; }
.readingContent01 .preWrap { background-color: #f5f5f5; }
.pageSwitch01 ul li a { padding: 10px 12px; font-weight: bold; }
}
