css:
@-moz-document url-prefix("http://cn.bing.com/search?q=") {
/*缩小到90%*/
body {
zoom: 90% !important;
}

.sb_results > li, .sc_hl1 > li{
  display:inline-block !important;
  width:50% !important;
  -moz-box-sizing:border-box !important;
  box-sizing:border-box !important;
  padding:10px !important;
  border:5px solid transparent !important;
  background-clip:padding-box !important;
  background-color:#F0F0F0!important;
  margin:0 !important;
  vertical-align:top !important;
  counter-increment:gOrder 1;
  outline:1px solid #E8E8E8;
  outline-offset:-5px;
}
  
div#sw_canvas{
  padding-left: 1px !important;
}
  

#sidebar,.sw_hdr_img,#sa_bop{
 display:none !important;
}

#results_area,.sc_hl1 > li{
    float: left !important;
	width: 100% !important;
}

.qscolumn{
  margin-right : 0px !important;
  width: 50% !important;
}


.sw_b > input.sw_qbox{
	min-width : 990px !important;
}
  
.sw_box >.sw_bd{
padding-top: 4px !important;
}
}

@-moz-document url-prefix("http://www.bing.com/search") {
#b_results > li, .sc_hl1 > li{
  display:inline-block !important;
  width:50% !important;
  -moz-box-sizing:border-box !important;
  box-sizing:border-box !important;
  padding:10px !important;
  border:5px solid transparent !important;
  background-clip:padding-box !important;
  background-color:#F0F0F0!important;
  margin:0 !important;
  vertical-align:top !important;
  counter-increment:gOrder 1;
  outline:1px solid #E8E8E8;
  outline-offset:-5px;
}
  

#b_context,#sw_tfbb{
 display:none !important;
}

#b_results, #b_content{
    float: left !important;
	width: 100% !important;
  	padding: 2px 3px !important;
}

.qscolumn{
  margin-right : 0px !important;
  width: 50% !important;
}

/*输入框*/
.b_searchboxForm > input#sb_form_q{
	min-width : 990px !important;
}
  
.sw_box >.sw_bd{
padding-top: 4px !important;
}

body{
	zoom: 90% !important;
}
}
