css:
@-moz-document
	url-prefix('https://www.tumblr.com/')
{

/* blue fix */
/*div.search_form_row*/#search_query {background-color: rgba(100,100,100,0.8)}
div.l-header .selection_nipple {border-bottom-color: rgba(100,100,100,0.8)}

#left_column, #right_column {
	background-color: transparent;
}
.controls_section.user_list li.item .follow_list_item_blog:before {
  background-image: none !important;
  border-right: none !important;
  }
.radar div.radar_footer .radar_avatar:before {
  background-image: none;
  }
.blog.has_follow_button:before,
#right_column:after {display: none}

.new_post_buttons.editing,
body:not(#tinymce) {
  background: left bottom lightblue fixed no-repeat url('https://31.media.tumblr.com/8ca8f5b6012ed2b279570ac5287bd75b/tumblr_nabywllkyz1rxghfko2_r1_1280.jpg') !important;
  background-size: cover !important;
}

.l-content {
  background: rgba(100,100,100,0.8) !important;
}

#new_post_notice_container,
#new_post_notice_container a {
  color:#fff !important
}

DIV.post.new_post_buttons {
  background: white center no-repeat url('https://38.media.tumblr.com/5ccf7712be5b8ae6c97e237417ad45bc/tumblr_nabywllkyz1rxghfko1_500.png');
}
.new_post_label:hover > SPAN {
  color:#000;
  background-color: rgba(192,192,192,.7);
}

.new_post_label{
  padding:10px 1px 69px 0 !important;
  background-clip: content-box;
}
.new_post_label > I::before {content: none}
.new_post_label > SPAN {
  font-size:14px;
  font-family:Georgia, Times New Roman, sans-serif;
  font-style:italic;
  text-transform: lowercase;
  color:#222;
  letter-spacing:1px;
}

}
