css:
@-moz-document url-prefix("http://www.realitymod.com/") {
body {font-size: 11px !important;}

#copyright, #footer, #primary-nav-features, #primary-nav-about, #primary-nav-media, #primary-nav-donate, #primary-nav-news, #sidebar, div#body-top, div#body_bottom, h2.title, div.fr, div.news-foot, a.feed.small, div.toggle-btn.news-toggle.active {display:none !important;}

div.news-body, .content {border:none !important;}

object#pr-header {
	visibility: hidden !important;
	height: 0px !important
}

div#primary-nav, div#outer_wrapper, div#wrapper, div#container {background: none !important;}

h2.news-title {padding: 5px 10px !important;}

div#container {
	margin: 0 auto !important;
	float: none !important;	 
}
}
