css:
@-moz-document domain("netafull.net") {

  header, #head_background, .top_banner, nav, #sub_conteiner_left,
  #sub_conteinner, #footer_area, .entry_head_share_btn, #zenback,
  .fb_iframe_widget, .newentry, .pagenate, ins
  { display: none !important; }

  #conteiner, #left-block {
    width: 660px !important;
  }
  #main_conteiner {
    width: inherit;
    background-image: none !important;
    background-color: #fff !important;
    margin: 2em auto !important;
    padding: 0 !important;
  }
  .entry_head {
    width: auto;
    margin: 0 !important;
    padding: 40px !important;
  }
  .entry_meta {
    background: none !important;
    box-shadow: none !important;
  }
  body {
    background-color: #f2f2f2 !important;
    color:#555;
  }
  .postbody {
    padding: 0 40px !important;
  }

}
