css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')  

{

.controls_section li .followers:before 
{
position: absolute !important;
height: 30px !important;
line-height: 28px !important;
width: 97px !important;
font: inherit !important;
}

a.followers  div.hide_overflow
{
visibility: hidden !important;
}

.controls_section li .followers:before 
{
content: "Sour Wolves" !important;
}

li.blog-sub-nav-item:nth-child(2) > a:nth-child(1) > div:nth-child(1):before
{
position: absolute !important;
height: 24px !important;
line-height: 28px !important;
width: 100px !important;
font: inherit !important;
background-color: #FFF !important;
}

li.blog-sub-nav-item:nth-child(2) > a:nth-child(1) > div:nth-child(1):hover:before
{
background-color: #E6EFF7 !important;
}

li.blog-sub-nav-item:nth-child(2) > a:nth-child(1) > div:nth-child(1):before
{
content: "Sour Wolves" !important;
}
}
