css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("iptorrents.com"), domain("iptorrents.ru"), domain("iptorrents.me"), domain("iptorrents.eu"), domain("iptorrents.us") {

/* DONT DONATE !!! */
  
/* THE MOST IMPORTANT STUFF TO REMOVE */
a[href$="/donate.php"] img { display: none; } a[href*="/donate.php"]{display: none;}
a[href$="/donateBTC.php"] img { display: none; } a[href*="/donateBTC.php"]{display: none;}
html > body > div#body > center > p[align="center"] { display: none !important; }
html > body > div#body > center > table[width*="1000"] { display: none !important; }
html > body > div#body > center > table.t0 tbody tr:nth-child(6) { display: none !important; }

/* remove shit banner */
.site_header tr:nth-child(1), .site_header tr:nth-child(2), .site_header tr:nth-child(3),  .site_header tr:nth-child(6) {
display: none;}
.site_header tr:nth-child(2) td img, .site_header tr:nth-child(5) td img {
display: none;}

/* remove useless announcement on top */
html > body > center > br { display: none !important; } html > body > center > b { display: none !important; } html > body > center > h1 { display: none !important; }
  
/* no need home */
.site_header tr:nth-child(5) td:nth-child(1) a:after {  content: "Browse";  font-weight: bold;}
.site_header tr:nth-child(5) td:nth-child(2) a:after {  content: "Movies";}
.site_header tr:nth-child(5) td:nth-child(4) a:after {  content: "TV Shows";}
.site_header tr:nth-child(5) td:nth-child(6) a:after {  content: "Forums";}
.site_header tr:nth-child(5) td:nth-child(8) a:after {  content: "RSS";}
.site_header tr:nth-child(5) td:nth-child(10) a:after {  content: "TV Guide";}

/* misc */
div[id="sponsorad"], div[id="blockblockA"], button[class="tnxBtn i0"],
iframe[id="Frame"] {
display: none !important
}

#iptStart .banner { height: 0px; background: #000 no-repeat 0 0;}
#iptStart .butRow a .b1 , .b2 , .b3 , .b4 , .b5 , .b6 , .b7 { display: none !important } 
div.butRow span{ display: none !important }

/* im lazy */
a[href$="mark-read"]::before { content: "CLEAR TAG"; }
a[href$="mark-read"] > img { display: none; }

}
