css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gizmodo.com"), domain("lifehacker.com") {

div > [id="header_container"],
div > [id="logobar"],
div > [id="sidebar"],
div > [id="timestamp"],
div > [id="footer"],
hr[class="separator"],
div > [class="metadata"]

{ display: none !important; }

div > [id="content"],
div > [id="main"],
div > [class="excerpt"]

{ width: auto !important; }

div > [class="post"],div > [class="post highlited"], div > [class="post-quicklinks quicklink"] { 
  border: 1px solid #d1d8dA !important;
  padding: 3px !important;
}

#content_container { background-image: None !important; }

}
