css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("turbobit.net") {
/* 1st page. Free or Pro */
#turbo-table tr:not(:first-child):not(:last-child)
,#turbo-table tr:last-child td:last-child
,.download-gate-info-cell
,.download-type
,.max-speed-time
,.wrap > center
,.footer

/* Subscription */
,.subs-top-banner
,#subs-main-header
,a[href="/payments/sms/checkSmsPin/"]
,.subs-form
,.subs-rules-block
,.main-info-hr
,#subs-small-text

/* 2nd page. Capcha */
,#download-time-premium
,#download-premium-btn
,.payment-block
,.col-2
/*,.download-area-top
,#body-link*/

/* 3rd page */
/*,[href="/turbo?gaid=free_1time60"]
,#premium-download-info
,object*/
{display:none !important}

/* 1st page */
#turbo-table {width: auto}
/* subscription text */
.bg-top-right {font-size: 0 !important;}

/* Visual hacks for wide text captcha */
#captcha-img, #captcha-img img {width: 50% !important; height: auto !important;}
#button1, #captcha_response, .submit-button {width: 100% !important}
.captcha_text {margin-top: 0 !important}
#download-main-info {border: none}
.core.inner {min-height: auto}
html {background: none}
}
