css:
body {
  -webkit-font-smoothing: subpixel-antialiased !important;
}

/* issue body */
.fsi-card {
  /* remove background */
  background: white !important;
  /* remove border */
  border: 0 !important;
  /* remove bottom horizontal line */
  -webkit-box-shadow: none !important;
  -moz-box-shadow: none !important;
  box-shadow: none !important;
}

/* remove useless "Attach" button */
.sb-issue-edit-add-attach {
  display: none !important;
}

/* fix issue title - id and title font must be equal (as before) */
.issue-summary_fsi, .issueId_fsi {
  font-weight: normal !important;
  font-size: 22px !important;
  color: #333 !important;
}

/* fix issue property names font â€” should be readable (as before) */
.fsi-property-label, .fsi-properties-line {
  color: #333 !important;
}
