css:
div .posthead {

	height: 30px !important;
  
}

div#time_image {
  
	width: 750px !important;
  	float: none !important;
  
}

span .date {

 	margin-left: 24px !important; 
 	font: normal 12px Tahoma,Calibri,Verdana,Geneva,sans-serif !important;
  
}

ol#posts .postbitlegacy {
 
    border-bottom-width: 1px !important;
  	margin-bottom: 10px !important;  	
  
}

ol#posts .postbitlegacy .posthead .postdate {
  
	font: normal 12px Tahoma,Calibri,Verdana,Geneva,sans-serif !important;
      
}

.nodecontrols a {
  	
  	color: rgb(68,68,68) !important;
    font-size: 12px !important;
  
}

div .postdetails {
 
  	background: rgb(255,255,255) !important;
  
}

#userdata {
  
	padding-top: 0px !important;
  	text-align: left !important;
    
}

.userinfo, .usertitle {
  
    overflow:visible !important;
  
}

  
#userstats {
    
	margin: 0 !important;
    
}


ol#posts .postbitlegacy .postdetails .postbody .postrow {
  
 	padding: 0px !important; 
  
}

div .postfoot {
  
  	background: rgba(255,255,255,0) !important;
 	padding: 0px !important; 
  
}

ol#posts .rating_results {
  
 	color: rgb(62,62,62) !important; 
  	top: 0px !important; 
  	font-weight: bold !important;
  
}
  
ol#posts a.rating_users {
	
	margin: -12px !important;
    color: rgb(17, 68, 119) !important;
    
}

.postcontrols {
  
	width: auto !important;
    padding-right: 10px !important;
    
}

.post_button {
  
 	float: left !important;
  	border: 0px !important;
  	background-color: rgba(0,0,0,0) !important;
	color: rgb(17, 68, 119) !important;
  	font: bold 11px Tahoma, Calibri, Verdana, Geneva, sans-serif !important;
    margin: 0 !important;
    padding: 0 !important;
  
}
  
.post_button:hover {
          
	text-decoration: underline !important;
        
}

.postrating {
    display: block !important;
}
