css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bangumi.tv"), domain("bgm.tv"), domain("chii.in") {
#prgManager #prgManagerMain {
  transition: height .3s;
}
#prgManager #listWrapper {
  height: 500px !important;
}
#prgManager #cloumnSubjectInfo .blockMode {
  transition: height .3s;
}
#prgManager:hover #prgManagerMain.blockModeWrapper {
  height: 500px !important;
}
#prgManager:hover #cloumnSubjectInfo .blockMode {
  height: 500px !important;
}
}
